LOAD DATA INFILE 'D:/WorkFile/TMP/auto.csv' INTO TABLE USER CHARACTER SET utf8 FIELDS TERMINATED BY ',' ESCAPED BY '"' ENCLOSED BY '"' LINES TERMINATED BY '\r\n' IGNORE 1 LINES (
  @idTMP,
  email,
  PASSWORD,
  username,
  role,
  STATUS,
  regtime,
  @regipTMP
) SET regip = IF (
  @regipTMP IS NULL,
  "3333",
  @regipTMP
),
id = IF(@idTMP = '', NULL, @idTMP)
